import React, { useState, useEffect } from "react";
import { getContract } from "./utils/contract";

function App() {
    const [credits, setCredits] = useState([]); // Хранение кредитов
    const [amount, setAmount] = useState("");  // Сумма нового кредита
    const [recipient, setRecipient] = useState(""); // Адрес получателя

    // Получение кредитов при загрузке страницы
    useEffect(() => {
        fetchCredits();
    }, []);

    // Получение списка кредитов
    const fetchCredits = async () => {
        try {
            const contract = getContract();
            const nextId = await contract.nextId(); // Получение ID следующего кредита
            const creditsArray = [];

            for (let i = 0; i < nextId; i++) {
                const credit = await contract.credits(i);
                creditsArray.push(credit);
            }

            setCredits(creditsArray);
        } catch (error) {
            console.error("Error fetching credits:", error);
        }
    };

    // Создание нового кредита
    const createCredit = async () => {
        try {
            const contract = getContract();
            const tx = await contract.createCredit(parseInt(amount));
            await tx.wait(); // Ждём завершения транзакции
            fetchCredits(); // Обновляем список кредитов
            setAmount(""); // Сбрасываем поле ввода
        } catch (error) {
            console.error("Error creating credit:", error);
        }
    };

    // Передача кредита другому адресу
    const transferCredit = async (id) => {
        try {
            const contract = getContract();
            const tx = await contract.transferCredit(id, recipient);
            await tx.wait(); // Ждём завершения транзакции
            fetchCredits(); // Обновляем список кредитов
            setRecipient(""); // Сбрасываем поле ввода
        } catch (error) {
            console.error("Error transferring credit:", error);
        }
    };

    return (
        <div style={{ padding: "20px" }}>
            <h1>Carbon Credit Management</h1>
            <div style={{ marginBottom: "20px" }}>
                <input
                    type="number"
                    placeholder="Enter credit amount"
                    value={amount}
                    onChange={(e) => setAmount(e.target.value)}
                    style={{ marginRight: "10px" }}
                />
                <button onClick={createCredit}>Create Credit</button>
            </div>
            <ul>
                {credits.map((credit, index) => (
                    <li key={index} style={{ marginBottom: "10px" }}>
                        <strong>ID:</strong> {credit.id.toString()} | 
                        <strong> Owner:</strong> {credit.owner} | 
                        <strong> Amount:</strong> {credit.amount.toString()}
                        <div style={{ marginTop: "5px" }}>
                            <input
                                type="text"
                                placeholder="Recipient address"
                                value={recipient}
                                onChange={(e) => setRecipient(e.target.value)}
                                style={{ marginRight: "10px" }}
                            />
                            <button onClick={() => transferCredit(credit.id)}>
                                Transfer
                            </button>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default App;
